import React, { useState } from "react";
import Board from "./Board";

const Game = () => {
  const [board, setBoard] = useState(Array(16).fill(null));
  const [xIsNext, setXIsNext] = useState(true);

  const handleClick = (i) => {
    const boardCopy = [...board];
    if (boardCopy[i]) return;
    if (boardCopy[i]) return;
    boardCopy[i] = xIsNext
      ? "fa-duotone fa-swords-laser"
      : "fa-brands fa-jedi-order";
    setBoard(boardCopy);
    setXIsNext(!xIsNext);
  };

  const resetGame = () => {
    return (
      <button
        className="reset-btn"
        onClick={() => setBoard(Array(16).fill(null))}
      >
        Reset Game
      </button>
    );
  };

  return (
    <div>
      <p className={"player"}>
        {"Next Player: " + (xIsNext ? "" : "")}
        <i
          className={
            xIsNext
              ? "fa-duotone fa-swords-laser " + winner
              : "fa-brands fa-jedi-order " + winner
          }
        ></i>
      </p>
      <div className="container">
        <div className="logo">
          <span>Tic Tac Toe</span>
        </div>
        <Board squares={board} onClick={handleClick} />
        {resetGame()}
      </div>
    </div>
  );
};

export default Game;
